import { _wcl } from './common-lib.js';
import { _wccss } from './common-css.js';

const defaults = {
  l10n: {
    confirm: 'OK',
    cancel: 'CANCEL'
  },
  confirm: false,
  cancel: false,
  sound: false,
  vibrate: false,
  show: false
};

const booleanAttrs = ['confirm', 'cancel', 'sound', 'vibrate', 'show'];
const objectAttrs = ['l10n'];
const custumEvents = {
  confirm: 'msc-reminder-confirm',
  cancel: 'msc-reminder-cancel'
};
const soundEffect = new Audio('data:audio/mp3;base64,');

const template = document.createElement('template');
template.innerHTML = `
<style>
${_wccss}

:host{position:fixed;inset-inline:0;inset-block-start:0;display:flex;pointer-events:none;user-select:none;-webkit-user-select:none;z-index:10;justify-content:center;}
.main {
  --margin: var(--msc-reminder-margin, 16px);
  --margin-block-start: calc(var(--margin) + var(--safe-area-top));
  --inline-size: fit-content;
  --max-inline-size: calc(100% - var(--safe-area-left) - var(--safe-area-right) - (var(--margin) * 2));
  --padding: var(--msc-reminder-padding, 24px);
  --border-radius: 42px;
  --transition-duration: .33s;
  --transition-function: cubic-bezier(0.666, 0, 0.4, 1.2);
  --shadow: var(--msc-reminder-shadow-color, 0 0 0);
  
  --scale-normal: .001;
  --scale-active: 1;
  --scale: var(--scale-normal);

  --opacity-normal: 0;
  --opacity-active: 1;
  --opacity: var(--opacity-normal);

  --pointer-events-normal: none;
  --pointer-events-active: auto;
  --pointer-events: var(--pointer-events-normal);

  /* functions */
  --button-ok-theme: 10 132 255;
  --button-ok-color: rgba(var(--button-ok-theme));
  --button-ok-bgc: rgba(var(--button-ok-theme)/.15);

  --button-cancel-color: rgba(255 255 255);
  --button-cancel-bgc: rgba(28 28 30);

  --button-overlay-opacity-normal: 0;
  --button-overlay-opacity-active: 1;
  --button-overlay-opacity: var(--button-overlay-opacity-normal);
}
.main{inline-size:var(--inline-size);max-inline-size:var(--max-inline-size);background-color:rgba(0 0 0);border-radius:var(--border-radius);padding:var(--padding);margin-block-start:var(--margin-block-start);box-sizing:border-box;scale:var(--scale);opacity:var(--opacity);pointer-events:var(--pointer-events);box-shadow:0 0 2px rgba(var(--shadow)/.05),0 4px 16px rgba(var(--shadow)/.2);}
.main{transform-origin:50% 0;transition:scale var(--transition-duration) var(--transition-function),opacity var(--transition-duration) var(--transition-function);will-change:scale,opacity;}
.main--active {
  --scale: var(--scale-active);
  --opacity: var(--opacity-active);
  --pointer-events: var(--pointer-events-active);
}
.main__slot{position:relative;inline-size:100%;display:block;}
.main__slot::slotted(*){min-inline-size:280px;max-inline-size:100%;}

.functions{margin-block-start:var(--margin);display:flex;gap:16px;}
.functions__button{position:relative;flex-grow:1;flex-basis:50%;color:var(--button-color);background:var(--button-bgc);block-size:50px;letter-spacing:1.2px;border-radius:50px;font-size:17px;line-height:1.2941;padding:0;appearance:none;border:0 none;box-shadow:none;margin:0;display:flex;align-items:center;justify-content:center;outline:0 none;overflow:hidden;}
.functions__button::before{position:absolute;inset-inline-start:0;inset-block-start:0;content:'';inline-size:100%;block-size:100%;display:block;pointer-events:none;background-color:rgba(116 116 128/.08);transition:opacity 200ms cubic-bezier(0.4, 0, 0.2, 1);opacity:var(--button-overlay-opacity);}
.functions__button:active{scale:.95;}
.functions__button[hidden]{display:none;}
.functions__button--ok {
  --button-color: var(--button-ok-color);
  --button-bgc: var(--button-ok-bgc);
}
.functions__button--cancel {
  --button-color: var(--button-cancel-color);
  --button-bgc: var(--button-cancel-bgc);
}
.functions__button:focus {
  --button-overlay-opacity: var(--button-overlay-opacity-active);
}
.functions[hidden]{display:none;}
.functions1:has(:nth-child(2 of [hidden])){display:none;}
.functions1:has([hidden]:nth-of-type(2)){display:none;}

@media (hover: hover) {
  .functions__button:hover {
    --button-overlay-opacity: var(--button-overlay-opacity-active);
  }
}
</style>

<div class="main" ontouchstart="">
  <slot class="main__slot"></slot>
  <div class="functions">
    <button type="button" value="cancel" class="functions__button functions__button--cancel" hidden>CANCEL</button>
    <button type="button" value="confirm" class="functions__button functions__button--ok" hidden>OK</button>
  </div>
</div>
`;

// Houdini Props and Vals
if (CSS?.registerProperty) {
  try {
    CSS.registerProperty({
      name: '--msc-reminder-margin',
      syntax: '<length>',
      inherits: true,
      initialValue: '16px'
    });

    CSS.registerProperty({
      name: '--msc-reminder-padding',
      syntax: '<length>',
      inherits: true,
      initialValue: '24px'
    });
  } catch(err) {
    console.warn(`msc-reminder: ${err.message}`);
  }
}

export class MscReminder extends HTMLElement {
  #data;
  #nodes;
  #config;

  constructor(config) {
    super();

    // template
    this.attachShadow({ mode: 'open', delegatesFocus: true });
    this.shadowRoot.appendChild(template.content.cloneNode(true));

    // data
    this.#data = {
      controller: ''
    };

    // nodes
    this.#nodes = {
      styleSheet: this.shadowRoot.querySelector('style'),
      main: this.shadowRoot.querySelector('.main'),
      functions: this.shadowRoot.querySelector('.functions'),
      btnConfirm: this.shadowRoot.querySelector('.functions__button--ok'),
      btnCancel: this.shadowRoot.querySelector('.functions__button--cancel')
    };

    // config
    this.#config = {
      ...defaults,
      ...config // new MscReminder(config)
    };

    // evts
    this._onClick = this._onClick.bind(this);
  }

  async connectedCallback() {
   const { config, error } = await _wcl.getWCConfig(this);

    if (error) {
      console.warn(`${_wcl.classToTagName(this.constructor.name)}: ${error}`);
      this.remove();
      return;
    } else {
      this.#config = {
        ...this.#config,
        ...config
      };
    }

    // upgradeProperty
    Object.keys(defaults).forEach((key) => this.#upgradeProperty(key));

    // evts
    this.#data.controller = new AbortController();
    const signal = this.#data.controller.signal;
    this.#nodes.functions.addEventListener('click', this._onClick, { signal });
  }

  disconnectedCallback() {
    if (this.#data?.controller) {
      this.#data.controller.abort();
    }
  }

  #format(attrName, oldValue, newValue) {
    const hasValue = newValue !== null;

    if (!hasValue) {
      if (booleanAttrs.includes(attrName)) {
        this.#config[attrName] = false;
      } else {
        this.#config[attrName] = defaults[attrName];
      }
    } else {
      switch (attrName) {
        case 'l10n':
          this.#config[attrName] = {
            ...defaults[attrName],
            ...JSON.parse(newValue)
          };
          break;

        case 'confirm':
        case 'cancel':
        case 'sound':
        case 'vibrate':
        case 'show':
          this.#config[attrName] = true;
          break;
      }
    }
  }

  attributeChangedCallback(attrName, oldValue, newValue) {
    if (!MscReminder.observedAttributes.includes(attrName)) {
      return;
    }

    this.#format(attrName, oldValue, newValue);

    switch (attrName) {
      case 'l10n':
        this.#nodes.btnConfirm.textContent = this.l10n.confirm;
        this.#nodes.btnCancel.textContent = this.l10n.cancel;
        break;

      case 'confirm':
        this.#nodes.btnConfirm.hidden = !this.confirm;
        this.#nodes.functions.hidden = !this.confirm && !this.cancel;
        break;

      case 'cancel':
        this.#nodes.btnCancel.hidden = !this.cancel;
        this.#nodes.functions.hidden = !this.confirm && !this.cancel;
        break;

      case 'show':
        this.#nodes.main.classList.toggle('main--active', this.show);
        
        if (this.show) {
          // sound
          if (this.sound) {
            soundEffect.play();
          }

          // vibrate
          if (navigator?.vibrate && this.vibrate) {
            navigator.vibrate(100);
          }
        }
        break;
    }
  }

  static get observedAttributes() {
    return Object.keys(defaults); // MscReminder.observedAttributes
  }

  #upgradeProperty(prop) {
    let value;

    if (MscReminder.observedAttributes.includes(prop)) {
      if (Object.prototype.hasOwnProperty.call(this, prop)) {
        value = this[prop];
        delete this[prop];
      } else {
        if (booleanAttrs.includes(prop)) {
          value = (this.hasAttribute(prop) || this.#config[prop]) ? true : false;
        } else if (objectAttrs.includes(prop)) {
          value = this.hasAttribute(prop) ? this.getAttribute(prop) : JSON.stringify(this.#config[prop]);
        } else {
          value = this.hasAttribute(prop) ? this.getAttribute(prop) : this.#config[prop];
        }
      }

      this[prop] = value;
    }
  }

  set l10n(value) {
    if (value) {
      const newValue = {
        ...this.l10n,
        ...(typeof value === 'string' ? JSON.parse(value) : value)
      };
      this.setAttribute('l10n', JSON.stringify(newValue));
    } else {
      this.removeAttribute('l10n');
    }
  }

  get l10n() {
    return this.#config.l10n;
  }

  set confirm(value) {
    if (value) {
      this.setAttribute('confirm', '');
    } else {
      this.removeAttribute('confirm');
    }
  }

  get confirm() {
    return this.#config.confirm;
  }

  set cancel(value) {
    if (value) {
      this.setAttribute('cancel', '');
    } else {
      this.removeAttribute('cancel');
    }
  }

  get cancel() {
    return this.#config.cancel;
  }

  set sound(value) {
    if (value) {
      this.setAttribute('sound', '');
    } else {
      this.removeAttribute('sound');
    }
  }

  get sound() {
    return this.#config.sound;
  }

  set vibrate(value) {
    if (value) {
      this.setAttribute('vibrate', '');
    } else {
      this.removeAttribute('vibrate');
    }
  }

  get vibrate() {
    return this.#config.vibrate;
  }

  set show(value) {
    if (value) {
      this.setAttribute('show', '');
    } else {
      this.removeAttribute('show');
    }
  }

  get show() {
    return this.#config.show;
  }

  #fireEvent(evtName, detail) {
    this.dispatchEvent(new CustomEvent(evtName,
      {
        bubbles: true,
        composed: true,
        ...(detail && { detail })
      }
    ));
  }

  _onClick(evt) {
    const button = evt.target.closest('button');

    if (!button) {
      return;
    }

    this.#fireEvent(custumEvents[button.value]);
    this.show = false;
  }

  dismiss() {
    this.show = false;
  }
}

// define web component
const S = _wcl.supports();
const T = _wcl.classToTagName('MscReminder');
if (S.customElements && S.shadowDOM && S.template && !window.customElements.get(T)) {
  window.customElements.define(_wcl.classToTagName('MscReminder'), MscReminder);
}